// Import React and other necessary modules

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  // State variables

  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState({ title: '', description: '' });
  const [editTask, setEditTask] = useState({ id: null, title: '', description: '' });

  // Fetch tasks on component mount

  useEffect(() => {
    axios.get('http://localhost:5000/tasks')
      .then(response => setTasks(response.data))
      .catch(error => console.error('Error fetching tasks:', error));
  }, []);

  // Event handlers for input changes and CRUD operations

  // ... (unchanged)

  // Component JSX

  return (
    <div className="app-container">
      <div className="blur-background">
        <h1>Task Manager</h1>
        <ul className="task-list">
          {/* Mapping through tasks and rendering task items */}
          {tasks.map(task => (
            <li key={task._id} className="task-item">
              {/* Edit mode */}
              {editTask.id === task._id ? (
                <div className="edit-task-form">
                  {/* Input fields for editing */}
                  <input
                    type="text"
                    name="title"
                    value={editTask.title}
                    onChange={(e) => setEditTask(prevState => ({ ...prevState, title: e.target.value }))}
                  />
                  <input
                    type="text"
                    name="description"
                    value={editTask.description}
                    onChange={(e) => setEditTask(prevState => ({ ...prevState, description: e.target.value }))}
                  />
                  {/* Save and Cancel buttons */}
                  <button className="save-button" onClick={handleSaveEdit}>Save</button>
                  <button className="cancel-button" onClick={handleCancelEdit}>Cancel</button>
                </div>
              ) : (
                // Display mode
                <div className="task-details">
                  <strong>{task.title}</strong>
                  <p>{task.description}</p>
                  {/* Edit and Delete buttons */}
                  <button className="edit-button" onClick={() => handleEditClick(task)}>Edit</button>
                  <button className="delete-button" onClick={() => handleDeleteTask(task._id)}>
                    Delete
                  </button>
                </div>
              )}
            </li>
          ))}
        </ul>
        {/* Add task form */}
        <div className="add-task-container">
          {/* Input fields for adding a new task */}
          <input
            type="text"
            name="title"
            value={newTask.title}
            onChange={handleInputChange}
            placeholder="Task title"
            className="input-field"
          />
          <input
            type="text"
            name="description"
            value={newTask.description}
            onChange={handleInputChange}
            placeholder="Task description"
            className="input-field"
          />
          {/* Add Task button */}
          <button onClick={handleAddTask} className="add-button">
            Add Task
          </button>
        </div>
      </div>
    </div>
  );
}

export default App;
