import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css'; // Import the stylesheet

function App() {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState({ title: '', description: '' });
  const [editingTask, setEditingTask] = useState(null);
  const [editedTitle, setEditedTitle] = useState('');
  const [editedDescription, setEditedDescription] = useState('');

  useEffect(() => {
    // Fetch tasks from the backend when the component mounts
    axios.get('http://localhost:5000/tasks')
      .then(response => setTasks(response.data))
      .catch(error => console.error('Error fetching tasks:', error));
  }, []); // Empty dependency array ensures the effect runs only once

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewTask(prevState => ({ ...prevState, [name]: value }));
  };

  const handleAddTask = () => {
    axios.post('http://localhost:5000/tasks', newTask)
      .then(response => {
        setTasks(prevTasks => [...prevTasks, response.data]);
        setNewTask({ title: '', description: '' });
      })
      .catch(error => console.error('Error adding task:', error));
  };

  const handleEditClick = (task) => {
    setEditingTask(task);
    setEditedTitle(task.title);
    setEditedDescription(task.description);
  };

  const handleSaveEdit = (task) => {
    axios.put(`http://localhost:5000/tasks/${task._id}`, {
      title: editedTitle,
      description: editedDescription,
    })
      .then(response => {
        setTasks(prevTasks =>
          prevTasks.map(t => (t._id === task._id ? response.data : t))
        );
        setEditingTask(null);
        setEditedTitle('');
        setEditedDescription('');
      })
      .catch(error => console.error('Error saving edited task:', error));
  };

  const handleDeleteTask = (taskId) => {
    axios.delete(`http://localhost:5000/tasks/${taskId}`)
      .then(response => {
        setTasks(prevTasks => prevTasks.filter(task => task._id !== taskId));
      })
      .catch(error => console.error('Error deleting task:', error));
  };

  // Log a message to indicate that the frontend is running
  console.log('Hello from frontend!');

  return (
    <div className="app-container">
      {/* ... rest of the component code ... */}
    </div>
  );
}

export default App;
