import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState({ title: '', description: '' });
  const [editTask, setEditTask] = useState({ id: null, title: '', description: '' });

  useEffect(() => {
    // Fetch tasks from the backend when the component mounts
    axios.get('http://localhost:5000/tasks')
      .then(response => setTasks(response.data))
      .catch(error => console.error('Error fetching tasks:', error));
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewTask(prevState => ({ ...prevState, [name]: value }));
  };

  const handleAddTask = () => {
    axios.post('http://localhost:5000/tasks', newTask)
      .then(response => {
        setTasks(prevTasks => [...prevTasks, response.data]);
        setNewTask({ title: '', description: '' });
      })
      .catch(error => console.error('Error adding task:', error));
  };

  const handleEditClick = (task) => {
    setEditTask({ id: task._id, title: task.title, description: task.description });
  };

  const handleCancelEdit = () => {
    setEditTask({ id: null, title: '', description: '' });
  };

  const handleSaveEdit = () => {
    axios.put(`http://localhost:5000/tasks/${editTask.id}`, editTask)
      .then(response => {
        setTasks(prevTasks => prevTasks.map(task => (task._id === editTask.id ? response.data : task)));
        setEditTask({ id: null, title: '', description: '' });
      })
      .catch(error => console.error('Error updating task:', error));
  };

  const handleDeleteTask = (taskId) => {
    axios.delete(`http://localhost:5000/tasks/${taskId}`)
      .then(response => {
        setTasks(prevTasks => prevTasks.filter(task => task._id !== taskId));
      })
      .catch(error => console.error('Error deleting task:', error));
  };

  return (
    <div className="app-container">
      <h1>Task Manager</h1>
      <ul className="task-list">
        {tasks.map(task => (
          <li key={task._id} className="task-item">
            {editTask.id === task._id ? (
              <div>
                <input
                  type="text"
                  name="title"
                  value={editTask.title}
                  onChange={(e) => setEditTask(prevState => ({ ...prevState, title: e.target.value }))}
                />
                <input
                  type="text"
                  name="description"
                  value={editTask.description}
                  onChange={(e) => setEditTask(prevState => ({ ...prevState, description: e.target.value }))}
                />
                <button onClick={handleSaveEdit}>Save</button>
                <button onClick={handleCancelEdit}>Cancel</button>
              </div>
            ) : (
              <div>
                <strong>{task.title}</strong>
                <p>{task.description}</p>
                <button onClick={() => handleEditClick(task)}>Edit</button>
                <button className="delete-button" onClick={() => handleDeleteTask(task._id)}>
                  Delete
                </button>
              </div>
            )}
          </li>
        ))}
      </ul>
      <div className="add-task-container">
        <input
          type="text"
          name="title"
          value={newTask.title}
          onChange={handleInputChange}
          placeholder="Task title"
          className="input-field"
        />
        <input
          type="text"
          name="description"
          value={newTask.description}
          onChange={handleInputChange}
          placeholder="Task description"
          className="input-field"
        />
        <button onClick={handleAddTask} className="add-button">
          Add Task
        </button>
      </div>
    </div>
  );
}

export default App;
